<?php//----- EXAMPLES -----// All controllers used for cloning should extend CloneNjin.// Controller will automatically be included and instantiated // at run time if the current view directory is "Page"
class Page extends CloneNjin
{		//Method will automatically be called at run time if 	//the current view is "Page/index.php"	public function index()	{		$this->registerKey('header', $this->parse(TEMPLATES.DS.'header.html'));		$this->registerKey('footer', $this->parse(TEMPLATES.DS.'footer.html'));		$this->registerKey('title', 'Home');		$this->registerKey('content', 'Welcome to the home page!');				$this->loadClone();	}		//Method will automatically be called at run time if 	//the current view is "Page/about.php"	public function about()	{		$this->registerKey('fact', 'I love to code!');		$this->registerKey('header', $this->parse(TEMPLATES.DS.'header.html'));		$this->registerKey('footer', $this->parse(TEMPLATES.DS.'footer.html'));		$this->registerKey('title', 'About');		$this->registerKey('content', 'This is arbitrary content on the about page!');				$this->loadClone();	}	}