<?php//----- EXAMPLES -----// All controllers used for cloning should extend CloneNjin.// Controller will automatically be included and instantiated // at run time if the current view directory is "Welcome"class Welcome extends CloneNjin{		//Method will automatically be called at run time if the 	//current view is "Welcome/index.php"	public function index()	{		$this->registerKey('header', $this->parse(TEMPLATES.DS.'header.html'));		$this->registerKey('footer', $this->parse(TEMPLATES.DS.'footer.html'));		$this->registerKey('title', 'Welcome');		$this->registerKey('content', 'Welcome to CloneNjin!');				$this->loadClone();	}}